
import base64
from io import BytesIO
from PIL import Image

# Remplacez 'votre_texte_base64' par le texte base64 de votre image
base64_text = ""

# DÃ©coder le texte base64 en bytes
image_bytes = base64.b64decode(base64_text)

# Charger les bytes dans une image PIL
image = Image.open(BytesIO(image_bytes))

# Afficher l'image
image.show()
